<script>
import Sidebar from "./layout/Sidebar.vue";
import Topnav from "./layout/Topnav.vue";

export default {
  name: "Dashboard",
  data() {
    return {
      active: false
    };
  },
  components: {
    Sidebar,
    Topnav
  },
  mounted() {
    this.$on("toggleNav", () => {
      this.active = !this.active;
    });
  },
  methods: {
    getClass() {
      if (this.active === true) {
        return "is-active-sidebar";
      } else {
        return "is-hide-sidebar";
      }
    }
  }
};
</script>


